def checkArguments(args:[]):
    if len(args) == 2: 
        dirname = os.path.dirname(args[1])
        if dirname:
            if not os.path.exists(dirname):
                print("The directory specified does not exist")
                exit(2)  
    elif len(args) == 3:
        dirname = os.path.dirname(args[2])
        if dirname:
            if not os.path.exists(dirname):
                print("The directory specified does not exist")
                exit(2)
        format = args[1].lstrip().rstrip().lower()  
        if format != "text" and format != "xml" and format != "json" and format != "all":
            print("Invalid or unknown format specified") 
            exit(3)
    else:
        print("Arguments exceeds") 
        exit(3) 

def writeTables (path ):
	mimeArray = mimes.split("\n")[:-1]
	file = open(path, "w")
	mimeArrayJson = []
	for mime in mimeArray:
		parts = mime.split('|') 
		print('{"%s", "%s", "%s", "%s"},' % (parts[0], parts[1], parts[2].replace('"', '\\"'), parts[3]), end="\n", file=file) 
		
def writeTextFile( path):
	print("Writing text to ", path, ".....\n")
	file=open(path, "w")
	print(mimes.replace("|", "\t"), sep="", end="", file=file, flush=True)
	file.close()
 
def writeXmlFile(path):
	print("Writing xml to ", path, ".....\n")
	mimeArray = mimes.split("\n")[:-1]
	file = open(path, "w")
	file.write("<?xml version='1.0' encoding='utf-8' ?>\n")
	file.write("<mimetypes>\n")
	for items in mimeArray:
		res = items.split('|')
		towrite = """\t<mimetype>
\t\t<extension>{0[0]}</extension>
\t\t<name>{0[1]}</name>
\t\t<type>{0[2]}</type>
\t</mimetype>
"""
		towrite = towrite.format(res)
		file.write(towrite) 
	file.write("</mimetypes>")
	file.close() 

def writeJSONFile(path):
	print("Writing json to ", path, ".....\n")
	mimeArray = mimes.split("\n")[:-1]
	file = open(path, "w")
	file.write('{\n\t"mimetype": ')
	mimeArrayJson = []
	for mime in mimeArray:
		parts = mime.split('|') 
		mimeArrayJson.append({"extension":parts[0], "name":parts[1], "type":parts[2]}) 
	print(json.dumps(mimeArrayJson, indent=4, separators=(',', ":")), end="", 
	sep="", file=file)
	file.write("}") 
	file.close() 


def main():
	if len(sys.argv) < 2:
		print("Usage : formatMimes.py [format] [dest file]") 
		exit(1)
	
	checkArguments(sys.argv)

	if len(sys.argv) == 2:
		format = "text"
		destPath = sys.argv[1]
	else:
		format = sys.argv[1].lower().strip().lstrip() 
		destPath = sys.argv[2] 

	if format == "text": writeTextFile(destPath) 
	elif format == "xml" : writeXmlFile(destPath)
	elif format == "json": writeJSONFile(destPath) 
	elif format == "all" :
	    writeJSONFile(destPath + ".json")
	    writeXmlFile(destPath + ".xml")
	    writeTextFile(destPath + ".txt")

if __name__ == '__main__':

    try:
        writeTables("./tables.txt");
    except FileNotFoundError:
        print("The file was not found")
    except FileExistsError:
        print("File Already Exists")
    except OSError:
        print("An operating system error occured. You may lack permissions")


